mapper.select:
SELECT 

FROM vending_machine a

WHERE a.id = #{id}

SELECT 

FROM vending_machine a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE vending_machine SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM vm_group a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE #{name}||'%' #{name}+'%' concat(#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
